import React,{useState,useEffect} from 'react'
import { Link } from 'react-router-dom'

function Bookings() {
const [bookings ,setBooking] = useState([])

useEffect(()=>{
  fetch("http://localhost:3000/bookings")
  .then(res =>{
    if(res.ok){
     res.json().then(bookings =>setBooking(bookings))
    }else
  { console.log("there was an error")}
  }
  )
},[])
let bookingList = bookings.map(booking =><div className='rounded-md bg-zinc-100'>
  <p className='m-3'><span className='text-orange-400'>Enjoy your trip From: </span>{booking.trip.route.start} <span className='text-orange-400'>To: </span>{booking.trip.route.destination}</p>
  <p className='m-3 flex flex-row justify-evenly'><span className='text-orange-400'> The Driver's Phone Number is: </span><span>{booking.trip.bus.driver_number}</span> </p>
<p className='m-3'><span className='text-orange-400'> You will be Travelling with bus of Number Plate: </span>{booking.trip.bus.number_plate}</p>
<p className='justify-around'><button className='bg-sky-500 rounded-md'><Link to="">More Details</Link></button></p>
</div>)
  return (
    <div className='grid grid-cols-2 gap-4'>
      {bookingList}
    </div>
  )
}

export default Bookings
